struct Node {
  int data;
  struct Node* next;
};
void traverse(struct Node* head) {
  while (head != NULL) {
    printf("%d ", head->data);
    head = head->next->next;
  }
}
int main() {
  struct Node n1 = {1, NULL}, n2 = {2, NULL}, n3 = {3, NULL}, n4 = {4, NULL};
  n1.next = &n2;
  n2.next = &n3;
  n3.next = &n4;
  traverse(&n1);
  return 0;
}

